law auth_decision_audit:1.0.0{
  scope: auth.decision;
  clock: realtime;
  if deny: span.status == 'denied' and span.metadata.route like '/api/boot%' then append_ledger(tag='auth.deny.kernel'), notify(role='security');
  if deny2: span.status == 'denied' and span.metadata.reason in ['token_expired','token_inactive'] then append_ledger(tag='auth.deny.token'), notify(role='ops');
  if permit: span.status == 'permitted' then append_ledger(tag='auth.permit');
}
